angular.module("truncate",[]).filter("characters",function(){return function(a,b){if(isNaN(b))return a;if(0>=b)return"";if(a&&a.length>=b){a=a.substring(0,b);var c=a.lastIndexOf(" ");return-1!==c&&(a=a.substr(0,c)),a+"..."}return a}}).filter("words",function(){return function(a,b){if(isNaN(b))return a;if(0>=b)return"";if(a){var c=a.split(/\s+/);c.length>b&&(a=c.slice(0,b).join(" ")+"...")}return a}});